package com.biz.hellojava;

public class Class12 {

	public static void main(String[] args) {
		System.out.println("50 + 30 = " + (50+30));
		System.out.println("50 - 30 = " + (50-30));
		System.out.println("50 * 30 = " + (50*30));
		System.out.println("50 / 30 = " + (50/30));
		System.out.println("50 % 30 = " + (50%30)); // 50을 30으로 나눈 나머지. modulation
		System.out.println("50 / 30 = " + (50/30) + "..." + (50%30));
		// TODO Auto-generated method stub
		// 숫자 50과 30의 덧셈, 뺄셈, 곱셈, 나눗셈 결과를 콘솔에 표시하는 코드를 지금부터 작성해 보세요
	}

}
